import { useState, useEffect } from 'react';

/**
 * useAuth จะคืน { user, setUser }
 * เมื่อ login สำเร็จให้เก็บ token ใน localStorage
 * และ decode payload ของ JWT เพื่อดึง username, role มาเก็บใน state
 */
export default function useAuth() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        // ถ้าคุณต้องการ decode JWT payload แบบง่าย:
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUser({ username: payload.username, role: payload.role });
      } catch {
        setUser(null);
      }
    }
  }, []);

  return { user, setUser };
}
