// src/components/AdminLayout.jsx
import React, { useState } from 'react'
import { NavLink, Outlet, useNavigate } from 'react-router-dom'
import {
  Menu,
  X,
  Home,
  Users,
  Box,
  List,
  ShoppingCart,
  Star,
  Truck,
  LogOut,
} from 'lucide-react'
import { jwtDecode } from 'jwt-decode'

export default function AdminLayout() {
  const [open, setOpen] = useState(false)
  const navigate = useNavigate()

  // pull username out of the JWT
  const token = localStorage.getItem('token')
  const username = token ? jwtDecode(token).username : ''

  const handleLogout = () => {
    localStorage.removeItem('token')
    navigate('/login', { replace: true })
  }

  const adminLinks = [
    { to: '/admin/dashboard', icon: List, label: 'Dashboard' },
    { to: '/admin/customers', icon: Users, label: 'Customer' },
    { to: '/admin/books', icon: Box, label: 'Books' },
    { to: '/admin/categories', icon: List, label: 'Category' },
    { to: '/admin/orders', icon: ShoppingCart, label: 'Order' },
    { to: '/admin/reviews', icon: Star, label: 'Rating & Review' },
    { to: '/admin/shipping', icon: Truck, label: 'Shipping & Delivery' },
    { to: '/admin/promotions', icon: Box, label: 'Promotion' },
  ]

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <aside
        className={
          'bg-gray-100 p-4 flex flex-col transition-transform ' +
          (open ? 'translate-x-0' : '-translate-x-full') +
          ' md:translate-x-0 md:w-60'
        }
      >
        {/* Sidebar header */}
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center space-x-2">
            <Users className="h-6 w-6 text-gray-600" />
            <span className="font-semibold text-gray-800">
              Hello, {username || 'Admin'}
            </span>
          </div>
          <button
            onClick={() => setOpen(false)}
            className="md:hidden p-2 text-gray-600"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Navigation links */}
        <nav className="flex-1 space-y-4">
          {adminLinks.map(({ to, icon: Icon, label }) => (
            <NavLink
              key={to}
              to={to}
              className={({ isActive }) =>
                'flex items-center px-2 py-1 rounded ' +
                (isActive
                  ? 'bg-blue-100 text-blue-600'
                  : 'text-gray-700 hover:bg-gray-200')
              }
            >
              <Icon className="h-5 w-5 mr-2" />
              {label}
            </NavLink>
          ))}
        </nav>

        {/* Logout button */}
        <button
          onClick={handleLogout}
          className="mt-auto flex items-center text-red-500 hover:underline"
        >
          <LogOut className="h-5 w-5 mr-2" />
          Logout
        </button>
      </aside>

      {/* Main content */}
      <div className="flex-1">
        {/* Top bar */}
        <header className="border-b p-4 flex items-center justify-between">
          <button onClick={() => setOpen(true)} className="md:hidden p-2">
            <Menu className="h-6 w-6 text-gray-600" />
          </button>
          <Home
            className="h-8 w-8 text-gray-600 cursor-pointer"
            onClick={() => navigate('/admin/dashboard')}
          />
        </header>

        {/* Page outlet */}
        <main className="p-6">
          <Outlet />
        </main>
      </div>
    </div>
  )
}
