import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ShoppingCart, User as UserIcon, Menu, Home, X } from 'lucide-react';
import useAuth from '../hooks/useAuth';

export default function Layout({ children }) {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const { user, setUser } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    // ลบ token, เคลียร์ user state แล้วไปหน้า login
    localStorage.removeItem('token');
    setUser(null);
    setDrawerOpen(false);
    navigate('/login');
  };

  return (
    <div className="flex min-h-screen flex-col bg-white">
      {/* Top Navigation Bar */}
      <header className="border-b bg-white">
        <div className="container mx-auto px-4">
          <div className="flex h-16 items-center justify-between">
            {/* เมนู Hamburger + Home */}
            <div className="flex items-center">
              <button
                className="md:hidden p-2"
                onClick={() => setDrawerOpen(true)}
              >
                <Menu className="h-6 w-6 text-gray-600" />
              </button>
              <Link to="/" className="flex items-center ml-2">
                <Home className="h-8 w-8 text-gray-600" />
              </Link>
            </div>

            {/* Search bar */}
            <div className="flex-1 mx-8 hidden md:block">
              <div className="relative">
                <input
                  type="search"
                  placeholder="Search"
                  className="w-full max-w-xl border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
                />
                <button className="absolute right-2 top-1/2 transform -translate-y-1/2 p-2 md:hidden">
                  {/* ถ้าอยากใส่ไอคอนค้นหาในมือถือ */}
                </button>
              </div>
            </div>

            {/* Icons: User & Cart */}
            <div className="flex items-center space-x-4">
              <button
                className="p-2"
                onClick={() => navigate(user ? '/profile' : '/login')}
                title={user ? 'Go to Profile' : 'Sign In'}
              >
                <UserIcon className="h-6 w-6 text-gray-600 hover:text-gray-800" />
              </button>
              <button
                className="p-2"
                onClick={() => navigate('/cart')}
                title="Cart"
              >
                <ShoppingCart className="h-6 w-6 text-gray-600 hover:text-gray-800" />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Sidebar Drawer */}
      {drawerOpen && (
        <div className="fixed inset-0 z-40 flex">
          {/* Sidebar */}
          <aside className="w-64 bg-white shadow-lg p-6 flex flex-col">
            {/* Header */}
            <div className="flex items-center justify-between mb-8">
              {user ? (
                <div className="flex items-center space-x-2">
                  <UserIcon className="h-6 w-6 text-gray-600" />
                  <span className="font-semibold text-gray-800">
                    Hello, {user.username}
                  </span>
                </div>
              ) : (
                <div className="font-semibold text-gray-800">Welcome</div>
              )}
              <button className="p-2" onClick={() => setDrawerOpen(false)}>
                <X className="h-6 w-6 text-gray-600" />
              </button>
            </div>

            {/* Navigation Links */}
            <nav className="flex-1 space-y-4">
              {user ? (
                <>
                  <Link
                    to="/profile"
                    className="block px-2 py-1 hover:bg-gray-100 rounded text-gray-700"
                    onClick={() => setDrawerOpen(false)}
                  >
                    Settings
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="block px-2 py-1 text-red-500 hover:underline"
                  >
                    Sign Out
                  </button>
                </>
              ) : (
                <Link
                  to="/login"
                  className="block px-2 py-1 hover:bg-gray-100 rounded text-gray-700"
                  onClick={() => setDrawerOpen(false)}
                >
                  Sign In
                </Link>
              )}
            </nav>
          </aside>
          {/* Overlay: คลิกตรงนี้แล้วปิด drawer */}
          <div
            className="flex-1 bg-black/25"
            onClick={() => setDrawerOpen(false)}
          />
        </div>
      )}

      {/* Main Content */}
      <main className="flex-1 container mx-auto py-6 px-4">
        {children}
      </main>

      {/* Footer */}
      <footer className="bg-gray-100 py-6">
        <div className="container mx-auto px-4">
          {/* ใส่เนื้อหา footer ตามต้องการ */}
          <p className="text-center text-sm text-gray-600">
            © {new Date().getFullYear()} Bookstore. All rights reserved.
          </p>
        </div>
      </footer>
    </div>
  );
}
