// src/components/Layout.jsx
import React, { useState, useEffect } from 'react'
import { Link, Outlet, useNavigate } from 'react-router-dom'
import { ShoppingCart, User, Menu, Home, X, LogOut } from 'lucide-react'
import useAuth from '../hooks/useAuth'

export default function Layout() {
  const [drawerOpen, setDrawerOpen] = useState(false)
  const [categories, setCategories] = useState([])
  const { user, setUser } = useAuth()
  const navigate = useNavigate()

  // load categories on mount
  useEffect(() => {
    fetch('/api/categories')
      .then((r) => r.json())
      .then(setCategories)
      .catch(console.error)
  }, [])

  const handleLogout = () => {
    localStorage.removeItem('token')
    setUser(null)
    setDrawerOpen(false)
    navigate('/')
  }

  const goProfileOrLogin = () => {
    if (user) navigate('/profile')
    else      navigate('/login')
  }

  const goCart = () => {
    if (user) navigate('/cart')
    else      navigate('/login')
  }

  return (
    <div className="flex min-h-screen flex-col">
      {/* ── Top Bar ───────────────────────────────────────── */}
      <header className="border-b">
        <div className="container mx-auto px-4">
          <div className="flex h-16 items-center justify-between">
            <div className="flex items-center">
              <button onClick={() => setDrawerOpen(true)} className="md:hidden p-2">
                <Menu className="h-6 w-6 text-gray-600" />
              </button>
              <Link to="/" className="flex items-center ml-2">
                <Home className="h-8 w-8 text-gray-600" />
              </Link>
            </div>
            <div className="flex-1 mx-8 hidden md:block">
              <input
                type="search"
                placeholder="Search"
                className="w-full max-w-xl border px-4 py-2 rounded"
              />
            </div>
            <div className="flex items-center space-x-4">
              <button onClick={goProfileOrLogin} className="p-2">
                <User className="h-6 w-6 text-gray-600" />
              </button>
              <button onClick={goCart} className="p-2">
                <ShoppingCart className="h-6 w-6 text-gray-600" />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* ── Drawer ────────────────────────────────────────── */}
      {drawerOpen && (
        <div className="fixed inset-0 z-40 flex">
          <aside className="w-64 bg-white p-6 flex flex-col shadow-lg">
            <div className="flex items-center justify-between mb-8">
              {user ? (
                <div className="flex items-center space-x-2">
                  <User className="h-6 w-6" />
                  <span>Hello, {user.username}</span>
                </div>
              ) : (
                <span>Welcome</span>
              )}
              <button onClick={() => setDrawerOpen(false)}>
                <X className="h-6 w-6" />
              </button>
            </div>

            <nav className="flex-1 space-y-2 overflow-auto">
              {categories.map((cat) => (
                <Link
                  key={cat.categoryID}
                  to={`/categories/${cat.categoryID}`}
                  className="block px-2 py-1 hover:bg-gray-100 rounded"
                  onClick={() => setDrawerOpen(false)}
                >
                  {cat.category_name}
                </Link>
              ))}
            </nav>

            <div className="mt-auto">
              {user ? (
                <button
                  onClick={handleLogout}
                  className="flex items-center space-x-1 text-red-500"
                >
                  <LogOut className="h-5 w-5" />
                  <span>Sign Out</span>
                </button>
              ) : (
                <Link
                  to="/login"
                  className="block px-2 py-1 hover:bg-gray-100 rounded"
                  onClick={() => {
                    setDrawerOpen(false)
                    navigate('/login')
                  }}
                >
                  Sign In
                </Link>
              )}
            </div>
          </aside>
          <div className="flex-1" onClick={() => setDrawerOpen(false)} />
        </div>
      )}

      {/* ── Here’s the magic: nested pages render into your layout! ── */}
      <main className="flex-1">
        <Outlet />
      </main>
    </div>
  )
}
