// src/components/ProtectedRoute.jsx
import React from 'react'
import { Navigate, useLocation } from 'react-router-dom'
import jwtDecode from 'jwt-decode'

/**
 * Wrap any routes you want protected by authentication.
 * If you pass a `role` prop, blocks users whose JWT.role !== that.
 */
export default function ProtectedRoute({ children, role }) {
  const location = useLocation()
  const token = localStorage.getItem('token')

  // 1) Not logged in? → Send to /login
  if (!token) {
    return <Navigate to="/login" state={{ from: location }} replace />
  }

  let payload
  try {
    payload = jwtDecode(token)
  } catch (e) {
    // invalid token → clear & send to /login
    localStorage.removeItem('token')
    return <Navigate to="/login" state={{ from: location }} replace />
  }

  // 2) If a role is required, enforce it:
  if (role && payload.role !== role) {
    // they’re not an admin → kick back to home
    return <Navigate to="/" replace />
  }

  // 3) All good → render children
  return children
}
