import React, { useState } from 'react';
import { Link, useNavigate, useLocation, Outlet } from 'react-router-dom';
import { ShoppingCart, User, Menu, Home, X, Search } from 'lucide-react';
import useAuth from '../hooks/useAuth';

export default function Layout() {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [user, setUser] = useAuth();
  const navigate = useNavigate();
  const { pathname } = useLocation();

  // ไม่โชว์ header บน login/register
  if (pathname === '/login' || pathname === '/register') return <Outlet />;

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/login');
  };

  return (
    <div className="flex min-h-screen flex-col">
      <header className="border-b bg-white">
        <div className="container mx-auto px-4">
          <div className="flex h-16 items-center justify-between">
            <div className="flex items-center">
              <button
                className="md:hidden p-2"
                onClick={() => setDrawerOpen(true)}
              >
                <Menu className="h-6 w-6 text-gray-600" />
              </button>
              <Link to="/" className="flex items-center ml-2">
                <Home className="h-8 w-8 text-gray-600" />
              </Link>
            </div>
            <div className="hidden md:flex flex-1 mx-4">
              <div className="relative w-full max-w-lg">
                <input
                  type="text"
                  placeholder="Search"
                  className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
                />
                <Search className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500" />
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <Link to="/profile" className="hidden md:block p-2">
                <User className="h-6 w-6 text-gray-600" />
              </Link>
              <Link to="/cart" className="p-2">
                <ShoppingCart className="h-6 w-6 text-gray-600" />
              </Link>
            </div>
          </div>
        </div>
      </header>

      {drawerOpen && (
        <div className="fixed inset-0 z-40 flex">
          <aside className="w-64 bg-white shadow-lg p-6 flex flex-col">
            <div className="flex items-center justify-between mb-8">
              {user ? (
                <div className="flex items-center space-x-2">
                  <User className="h-6 w-6 text-gray-600" />
                  <span className="font-semibold text-gray-800">
                    Hello, {user.username}
                  </span>
                </div>
              ) : (
                <div className="font-semibold text-gray-800">Welcome</div>
              )}
              <button
                className="p-2"
                onClick={() => setDrawerOpen(false)}
              >
                <X className="h-6 w-6 text-gray-600" />
              </button>
            </div>
            <nav className="flex-1 space-y-4">
              {user ? (
                <>
                  <Link
                    to="/profile"
                    onClick={() => setDrawerOpen(false)}
                    className="block px-2 py-1 hover:bg-gray-100 rounded"
                  >
                    Settings
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="block px-2 py-1 text-red-500 hover:underline"
                  >
                    Sign Out
                  </button>
                </>
              ) : (
                <Link
                  to="/login"
                  onClick={() => setDrawerOpen(false)}
                  className="block px-2 py-1 hover:bg-gray-100 rounded"
                >
                  Sign In
                </Link>
              )}
            </nav>
          </aside>
          <div
            className="flex-1"
            onClick={() => setDrawerOpen(false)}
          />
        </div>
      )}

      <main className="flex-1 container mx-auto px-4 py-6">
        <Outlet />
      </main>
    </div>
  );
}
