import React, { useEffect, useState } from 'react';
import api from '../../api/axios';

export default function DashboardPage() {
  const [stats, setStats] = useState({});

  useEffect(() => {
    api.get('/admin/dashboard').then(res => setStats(res.data));
  }, []);

  return (
    <div>
      <h1 className="text-2xl font-semibold mb-4">Dashboard</h1>
      <div className="grid grid-cols-4 gap-4">
        <div className="p-4 bg-blue-100 rounded">New Orders: {stats.newOrders}</div>
        <div className="p-4 bg-green-100 rounded">Bounce Rate: {stats.bounceRate}%</div>
        <div className="p-4 bg-yellow-100 rounded">User Registrations: {stats.userRegs}</div>
        <div className="p-4 bg-red-100 rounded">Unique Visitors: {stats.visitors}</div>
      </div>
    </div>
  );
}
