import React, { useState, useEffect } from 'react';
import api from '../api/axios';

export default function ProfilePage() {
  const [form, setForm] = useState({
    password: '',
    house_number: '',
    building: '',
    moo: '',
    village: '',
    soi: '',
    road: '',
    sub_district: '',
    district: '',
    province: '',
    postal_code: '',
  });

  // โหลดข้อมูลเดิมมาเติมในฟอร์ม
  useEffect(() => {
    api.get('/users/me').then(res => {
      setForm(f => ({ ...f, ...res.data }));
    });
  }, []);

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await api.put('/users/me', form);
      alert('อัปเดตข้อมูลสำเร็จ');
      const { data } = await api.get('/users/me');
      setForm(f => ({ ...f, ...data }));
    } catch {
      alert('อัปเดตไม่สำเร็จ');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 max-w-md mx-auto">
      <h2 className="text-2xl font-semibold">แก้ไขโปรไฟล์</h2>

      <div>
        <label className="block mb-1">Password</label>
        <input
          type="password"
          name="password"
          value={form.password}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <div>
        <label className="block mb-1">House #</label>
        <input
          name="house_number"
          value={form.house_number}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <div>
        <label className="block mb-1">Building</label>
        <input
          name="building"
          value={form.building}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <div>
        <label className="block mb-1">Moo</label>
        <input
          name="moo"
          value={form.moo}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <div>
        <label className="block mb-1">Village</label>
        <input
          name="village"
          value={form.village}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <div>
        <label className="block mb-1">Soi</label>
        <input
          name="soi"
          value={form.soi}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <div>
        <label className="block mb-1">Road</label>
        <input
          name="road"
          value={form.road}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <div>
        <label className="block mb-1">Sub-district</label>
        <input
          name="sub_district"
          value={form.sub_district}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <div>
        <label className="block mb-1">District</label>
        <input
          name="district"
          value={form.district}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <div>
        <label className="block mb-1">Province</label>
        <input
          name="province"
          value={form.province}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <div>
        <label className="block mb-1">Postal Code</label>
        <input
          name="postal_code"
          value={form.postal_code}
          onChange={handleChange}
          className="w-full border px-2 py-1 rounded"
        />
      </div>

      <button
        type="submit"
        className="px-4 py-2 bg-green-600 text-white rounded"
      >
        Save
      </button>
    </form>
  );
}
