// src/pages/ProfilePage.jsx
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import api from '../api/axios'

export default function ProfilePage() {
  const [profile, setProfile] = useState(null)
  const [loading, setLoading]   = useState(true)
  const navigate = useNavigate()

  useEffect(() => {
    const token = localStorage.getItem('token')
    if (!token) {
      // no token → redirect to login
      navigate('/login', { replace: true })
      return
    }

    // fetch the profile
    api
      .get('/users/profile', {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(res => {
        setProfile(res.data)
      })
      .catch(err => {
        console.error('Profile fetch failed:', err)
        // invalid token? clear + redirect
        localStorage.removeItem('token')
        navigate('/login', { replace: true })
      })
      .finally(() => {
        setLoading(false)
      })
  }, [navigate])

  // loading spinner
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <p className="text-gray-600">กำลังโหลดข้อมูลโปรไฟล์…</p>
      </div>
    )
  }

  // once loaded, `profile` should be non-null—otherwise we already redirected

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center px-4">
      <div className="bg-white w-full max-w-md p-8 rounded shadow">
        <h2 className="text-2xl font-semibold mb-6 text-center">Your Profile</h2>

        <div className="space-y-3 text-gray-800">
          <p>
            <span className="font-medium">Username:</span> {profile.username}
          </p>
          <p>
            <span className="font-medium">Email:</span> {profile.email}
          </p>
          <p>
            <span className="font-medium">First Name:</span> {profile.fname}
          </p>
          <p>
            <span className="font-medium">Last Name:</span> {profile.lname}
          </p>
          <p>
            <span className="font-medium">Phone:</span> {profile.phone}
          </p>
          {/* Add more fields if you need: address, etc. */}
        </div>

        <div className="mt-8 flex justify-between">
          <button
            onClick={() => navigate(-1)}
            className="px-4 py-2 border rounded hover:bg-gray-50"
          >
            Back
          </button>
          <button
            onClick={() => {
              localStorage.removeItem('token')
              navigate('/', { replace: true })
            }}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  )
}
