// src/components/Layout.jsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ShoppingCart, User as UserIcon, Menu, Home, X } from 'lucide-react';
import useAuth from '../hooks/useAuth';

export default function Layout({ children }) {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const { user, setUser } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setDrawerOpen(false);
    navigate('/login');
  };

  return (
    <div className="flex min-h-screen flex-col bg-white">
      <header className="border-b bg-white">
        <div className="container mx-auto px-4 flex items-center justify-between h-16">
          {/* Hamburger + Home */}
          <div className="flex items-center">
            <button
              className="md:hidden p-2"
              onClick={() => setDrawerOpen(true)}
              type="button"
            >
              <Menu className="h-6 w-6 text-gray-600" />
            </button>
            <Link to="/" className="ml-2">
              <Home className="h-8 w-8 text-gray-600" />
            </Link>
          </div>

          {/* Search (desktop only) */}
          <div className="hidden md:block flex-1 mx-8">
            <input
              type="search"
              placeholder="Search"
              className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
          </div>

          {/* Icons */}
          <div className="flex items-center space-x-4">
            {/* User Icon: ถ้า logged in → ไป /profile ถ้ายังไม่ login → ไป /login */}
            <Link
              to={user ? '/profile' : '/login'}
              className="p-2"
              title={user ? 'Go to Profile' : 'Sign In'}
            >
              <UserIcon className="h-6 w-6 text-gray-600 hover:text-gray-800" />
            </Link>

            {/* Cart */}
            <button
              className="p-2"
              onClick={() => navigate('/cart')}
              type="button"
              title="Cart"
            >
              <ShoppingCart className="h-6 w-6 text-gray-600 hover:text-gray-800" />
            </button>
          </div>
        </div>
      </header>

      {/* Sidebar Drawer */}
      {drawerOpen && (
        <div className="fixed inset-0 z-40 flex">
          <aside className="w-64 bg-white shadow-lg p-6 flex flex-col">
            <div className="flex items-center justify-between mb-8">
              {user ? (
                <div className="flex items-center space-x-2">
                  <UserIcon className="h-6 w-6 text-gray-600" />
                  <span className="font-semibold text-gray-800">
                    Hello, {user.username}
                  </span>
                </div>
              ) : (
                <div className="font-semibold text-gray-800">Welcome</div>
              )}
              <button
                onClick={() => setDrawerOpen(false)}
                type="button"
                className="p-2"
              >
                <X className="h-6 w-6 text-gray-600" />
              </button>
            </div>

            <nav className="flex-1 space-y-4">
              {user ? (
                <>
                  <Link
                    to="/profile"
                    className="block px-2 py-1 hover:bg-gray-100 rounded text-gray-700"
                    onClick={() => setDrawerOpen(false)}
                  >
                    Settings
                  </Link>
                  <button
                    onClick={handleLogout}
                    type="button"
                    className="block px-2 py-1 text-red-500 hover:underline"
                  >
                    Sign Out
                  </button>
                </>
              ) : (
                <Link
                  to="/login"
                  className="block px-2 py-1 hover:bg-gray-100 rounded text-gray-700"
                  onClick={() => setDrawerOpen(false)}
                >
                  Sign In
                </Link>
              )}
            </nav>
          </aside>
          <div
            className="flex-1 bg-black/25"
            onClick={() => setDrawerOpen(false)}
          />
        </div>
      )}

      <main className="flex-1 container mx-auto py-6 px-4">
        {children}
      </main>

      <footer className="bg-gray-100 py-6">
        <div className="container mx-auto px-4 text-center text-sm text-gray-600">
          © {new Date().getFullYear()} Bookstore. All rights reserved.
        </div>
      </footer>
    </div>
  );
}
