// src/pages/ProfilePage.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/axios';
import useAuth from '../hooks/useAuth';

export default function ProfilePage() {
  const { user, setUser } = useAuth();
  const [profile, setProfile] = useState(null);
  const navigate = useNavigate();

  // โหลดข้อมูล profile จาก backend
  useEffect(() => {
    if (!user) return;
    api
      .get('/users/profile', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      })
      .then(res => setProfile(res.data))
      .catch(err => {
        console.error(err);
        // ถ้า token หมดอายุหรือไม่ถูกต้อง ก็ logout ออก
        handleLogout();
      });
  }, [user]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/');  // กลับหน้า Home
  };

  // ถ้ายังไม่ login
  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
        <div className="bg-white p-8 rounded shadow text-center">
          <p className="mb-4">กรุณาเข้าสู่ระบบก่อนดูโปรไฟล์</p>
          <button
            onClick={() => navigate('/login')}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Sign In
          </button>
        </div>
      </div>
    );
  }

  // กำลังโหลดข้อมูล
  if (!profile) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <p className="text-gray-600">กำลังโหลดข้อมูล...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center px-4">
      <div className="bg-white w-full max-w-md p-8 rounded shadow">
        <h2 className="text-2xl font-semibold mb-6 text-center">Your Profile</h2>
        <div className="space-y-3 text-gray-800">
          <p><span className="font-medium">Username:</span> {profile.username}</p>
          <p><span className="font-medium">Email:</span> {profile.email}</p>
          <p><span className="font-medium">First Name:</span> {profile.fname}</p>
          <p><span className="font-medium">Last Name:</span> {profile.lname}</p>
          <p><span className="font-medium">Phone:</span> {profile.phone}</p>
          {/* เพิ่มฟิลด์อื่นๆ ได้ตาม schema */}
        </div>

        <div className="mt-8 flex justify-between">
          <button
            onClick={() => navigate(-1)}  // กลับหน้าเดิม
            className="px-4 py-2 border rounded hover:bg-gray-50"
          >
            Back
          </button>
          <button
            onClick={handleLogout}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
}
