// src/components/ProtectedRoute.jsx
import React from 'react';
import { Navigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

/**
 * Wrap any route that needs authentication (and optionally a role).
 *
 * @param {React.ReactNode} children     The component(s) to render if access is allowed.
 * @param {string}           requiredRole  (optional) e.g. "admin"
 */
export default function ProtectedRoute({ children, requiredRole }) {
  const token = localStorage.getItem('token');
  if (!token) {
    // Not logged in
    return <Navigate to="/login" replace />;
  }

  let decoded;
  try {
    decoded = jwtDecode(token);
  } catch (err) {
    // Bad token—purge & re-login
    console.error('Invalid token:', err);
    localStorage.removeItem('token');
    return <Navigate to="/login" replace />;
  }

  // If they need a specific role, but they don't have it, block
  if (requiredRole && decoded.role !== requiredRole) {
    return <Navigate to="/" replace />;
  }

  // All good → render the protected UI
  return <>{children}</>;
}
