// src/components/Layout.jsx
import React, { useState, useEffect } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { ShoppingCart, User, Menu, Home, X, LogOut } from 'lucide-react'
import useAuth from '../hooks/useAuth'

export default function Layout({ children }) {
  const [drawerOpen, setDrawerOpen] = useState(false)
  const [categories, setCategories] = useState([])
  const { user, setUser } = useAuth()
  const navigate = useNavigate()

  // 1. ดึงหมวดหมู่จาก backend
  useEffect(() => {
    fetch('/api/categories')            // <-- ต้องมี route GET /api/categories
      .then((res) => res.json())
      .then((data) => setCategories(data))
      .catch((err) => console.error('Failed to load categories', err))
  }, [])

  const handleLogout = () => {
    localStorage.removeItem('token')
    setUser(null)
    setDrawerOpen(false)
    navigate('/')
  }

  const goProfileOrLogin = () => {
    if (user) navigate('/profile')
    else      navigate('/login')
  }

  const goCart = () => {
    if (user) navigate('/cart')
    else      navigate('/login')
  }

  return (
    <div className="flex min-h-screen flex-col">
      {/* Top Bar */}
      <header className="border-b">
        <div className="container mx-auto px-4">
          <div className="flex h-16 items-center justify-between">
            {/* Hamburger + Home */}
            <div className="flex items-center">
              <button
                className="md:hidden p-2"
                onClick={() => setDrawerOpen(true)}
              >
                <Menu className="h-6 w-6 text-gray-600" />
              </button>
              <Link to="/" className="flex items-center ml-2">
                <Home className="h-8 w-8 text-gray-600" />
              </Link>
            </div>

            {/* Search bar (ขยายบน md+) */}
            <div className="flex-1 mx-8 hidden md:block">
              <div className="relative">
                <input
                  type="search"
                  placeholder="Search"
                  className="w-full max-w-xl border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
                />
                <button
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 p-2 md:hidden"
                >
                  🔍
                </button>
              </div>
            </div>

            {/* Icons: User + Cart */}
            <div className="flex items-center space-x-4">
              <button className="p-2" onClick={goProfileOrLogin}>
                <User className="h-6 w-6 text-gray-600" />
              </button>
              <button className="p-2" onClick={goCart}>
                <ShoppingCart className="h-6 w-6 text-gray-600" />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Drawer / Sidebar */}
      {drawerOpen && (
        <div className="fixed inset-0 z-40 flex">
          <aside className="w-64 bg-white shadow-lg p-6 flex flex-col">
            {/* Header */}
            <div className="flex items-center justify-between mb-8">
              {user ? (
                <div className="flex items-center space-x-2">
                  <User className="h-6 w-6 text-gray-600" />
                  <span className="font-semibold text-gray-800">
                    Hello, {user.username}
                  </span>
                </div>
              ) : (
                <div className="font-semibold text-gray-800">Welcome</div>
              )}
              <button className="p-2" onClick={() => setDrawerOpen(false)}>
                <X className="h-6 w-6 text-gray-600" />
              </button>
            </div>

            {/* Nav: Categories */}
            <nav className="flex-1 space-y-2 overflow-auto">
              {categories.map((cat) => (
                <Link
                  key={cat.categoryID}
                  to={`/categories/${cat.categoryID}`}
                  className="block px-2 py-1 hover:bg-gray-100 rounded text-gray-700"
                  onClick={() => setDrawerOpen(false)}
                >
                  {cat.category_name}
                </Link>
              ))}
            </nav>

            {/* Footer: Sign In / Out */}
            <div className="mt-auto">
              {user ? (
                <button
                  onClick={handleLogout}
                  className="flex items-center px-2 py-1 text-red-500 hover:underline space-x-1"
                >
                  <LogOut className="h-5 w-5" />
                  <span>Sign Out</span>
                </button>
              ) : (
                <Link
                  to="/login"
                  className="block px-2 py-1 hover:bg-gray-100 rounded text-gray-700"
                  onClick={() => {
                    setDrawerOpen(false)
                    navigate('/login')
                  }}
                >
                  Sign In
                </Link>
              )}
            </div>
          </aside>
          {/* Backdrop ปิด Drawer เมื่อคลิกพื้นที่ด้านนอก */}
          <div
            className="flex-1"
            onClick={() => setDrawerOpen(false)}
          />
        </div>
      )}

      {/* Main Content */}
      <main className="flex-1">{children}</main>
    </div>
  )
}
