// src/components/AdminLayout.jsx
import React, { useState } from 'react'
import { NavLink, Outlet, useNavigate } from 'react-router-dom'
import { Menu, X, Home, Users, Box, List, ShoppingCart, Star, Truck, LogOut } from 'lucide-react'

export default function AdminLayout() {
  const [open, setOpen] = useState(false)
  const navigate     = useNavigate()
  const token        = localStorage.getItem('token')
  const username     = token ? jwt_decode(token).username : ''

  const handleLogout = () => {
    localStorage.removeItem('token')
    navigate('/login', { replace: true })
  }

  const items = [
    ['Dashboard',   '/admin',         <List size={20}/>],
    ['Customers',   '/admin/customers', <Users size={20}/>],
    ['Books',       '/admin/books',     <Box size={20}/>],
    ['Categories',  '/admin/categories',<Home size={20}/>],
    ['Orders',      '/admin/orders',    <ShoppingCart size={20}/>],
    ['Reviews',     '/admin/reviews',   <Star size={20}/>],
    ['Shipping',    '/admin/shipping',  <Truck size={20}/>],
    ['Promotions',  '/admin/promotions',<Box size={20}/>],
  ]

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-50 border-r hidden md:flex flex-col">
        <div className="flex items-center space-x-2 p-4 border-b">
          <Home size={24}/>
          <span className="font-semibold">Hello, {username}</span>
        </div>
        <nav className="flex-1 p-4 space-y-2">
          {items.map(([name, to, icon]) => (
            <NavLink
              key={to}
              to={to}
              className={({isActive}) =>
                `flex items-center space-x-2 px-3 py-2 rounded hover:bg-gray-100 ${
                  isActive ? 'bg-gray-200 font-medium' : ''
                }`
              }
            >
              {icon}
              <span>{name}</span>
            </NavLink>
          ))}
        </nav>
        <button
          onClick={handleLogout}
          className="m-4 flex items-center space-x-2 text-red-600 hover:underline"
        >
          <LogOut size={20}/>
          <span>Logout</span>
        </button>
      </aside>

      {/* Mobile top bar */}
      <header className="md:hidden w-full bg-white border-b flex items-center justify-between p-2">
        <button onClick={() => setOpen(o => !o)}>
          {open ? <X size={24}/> : <Menu size={24}/>}
        </button>
        <span className="font-semibold">Admin</span>
      </header>

      {/* Mobile drawer */}
      {open && (
        <div className="md:hidden fixed inset-0 z-40 flex bg-black/20">
          <aside className="w-64 bg-gray-50 p-4">
            <div className="flex items-center justify-between mb-4">
              <span>Hello, {username}</span>
              <button onClick={()=>setOpen(false)}><X/></button>
            </div>
            {items.map(([name, to, icon]) => (
              <NavLink
                key={to}
                to={to}
                onClick={()=>setOpen(false)}
                className="flex items-center space-x-2 px-3 py-2 rounded hover:bg-gray-100"
              >
                {icon}
                <span>{name}</span>
              </NavLink>
            ))}
            <button
              onClick={handleLogout}
              className="mt-4 flex items-center space-x-2 text-red-600 hover:underline"
            >
              <LogOut/>
              <span>Logout</span>
            </button>
          </aside>
          <div className="flex-1" onClick={()=>setOpen(false)} />
        </div>
      )}

      {/* Main content */}
      <main className="flex-1 p-4 bg-white overflow-auto">
        <Outlet/>
      </main>
    </div>
  )
}
