// src/components/Layout.jsx
import React, { useState, useEffect } from 'react'
import { Link, useNavigate, Outlet } from 'react-router-dom'
import { ShoppingCart, User as UserIcon, Menu, Home, X } from 'lucide-react'
import useAuth from '../hooks/useAuth'
import api from '../api/axios'  // your axios instance pointing to backend

export default function Layout() {
  const [drawerOpen, setDrawerOpen] = useState(false)
  const [categories, setCategories] = useState([])
  const { user, setUser } = useAuth()
  const navigate = useNavigate()

  // โหลดหมวดหมู่จาก backend
  useEffect(() => {
    api
      .get('/books/categories')      // สมมติ endpoint นี้ return: ["Manga","Novel",...]
      .then(res => setCategories(res.data))
      .catch(console.error)
  }, [])

  const handleLogout = () => {
    localStorage.removeItem('token')
    setUser(null)
    setDrawerOpen(false)
    navigate('/')
  }

  return (
    <div className="flex flex-col min-h-screen bg-white">
      {/* Top Bar */}
      <header className="border-b bg-white">
        <div className="container mx-auto px-4 flex items-center justify-between h-16">
          <div className="flex items-center">
            <button onClick={() => setDrawerOpen(true)} className="md:hidden p-2" type="button">
              <Menu className="h-6 w-6 text-gray-600" />
            </button>
            <Link to="/" className="ml-2">
              <Home className="h-8 w-8 text-gray-600" />
            </Link>
          </div>
          <div className="hidden md:block flex-1 mx-8">
            <input
              type="search"
              placeholder="Search"
              className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
          </div>
          <div className="flex items-center space-x-4">
            <Link
              to={user ? '/profile' : '/login'}
              className="p-2"
              title={user ? 'Go to Profile' : 'Sign In'}
            >
              <UserIcon className="h-6 w-6 text-gray-600 hover:text-gray-800" />
            </Link>
            <button
              onClick={() => navigate('/cart')}
              className="p-2"
              type="button"
              title="Cart"
            >
              <ShoppingCart className="h-6 w-6 text-gray-600 hover:text-gray-800" />
            </button>
          </div>
        </div>
      </header>

      {/* Sidebar Drawer */}
      {drawerOpen && (
        <div className="fixed inset-0 z-40 flex">
          <aside className="w-64 bg-white shadow-lg p-6 flex flex-col">
            {/* Header */}
            <div className="flex items-center justify-between mb-6">
              {user ? (
                <div className="flex items-center space-x-2">
                  <UserIcon className="h-6 w-6 text-gray-600" />
                  <span className="font-semibold text-gray-800">
                    Hello, {user.username}
                  </span>
                </div>
              ) : (
                <div className="font-semibold text-gray-800">Hello, User</div>
              )}
              <button onClick={() => setDrawerOpen(false)} className="p-2" type="button">
                <X className="h-6 w-6 text-gray-600" />
              </button>
            </div>

            {/* Auth Buttons */}
            <nav className="mb-6">
              {user ? (
                <button
                  onClick={handleLogout}
                  className="w-full text-left px-2 py-1 text-red-600 hover:underline"
                  type="button"
                >
                  Logout
                </button>
              ) : (
                <Link
                  to="/login"
                  onClick={() => setDrawerOpen(false)}
                  className="block px-2 py-1 hover:bg-gray-100 rounded text-gray-700"
                >
                  Sign In
                </Link>
              )}
            </nav>

            {/* Categories */}
            <div className="mb-4">
              <h3 className="text-gray-600 uppercase tracking-wide mb-2">Categories</h3>
              <ul className="space-y-1">
                {categories.map(cat => {
                  // สร้าง slug-friendly path
                  const slug = cat.toLowerCase().replace(/\s+/g, '-')
                  return (
                    <li key={cat}>
                      <Link
                        to={`/category/${slug}`}
                        onClick={() => setDrawerOpen(false)}
                        className="block px-2 py-1 hover:bg-gray-100 rounded text-gray-800"
                      >
                        {cat}
                      </Link>
                    </li>
                  )
                })}
              </ul>
            </div>
          </aside>
          <div
            className="flex-1 bg-black/25"
            onClick={() => setDrawerOpen(false)}
          />
        </div>
      )}

      {/* Main Content */}
      <main className="flex-1 container mx-auto py-6 px-4">
        <Outlet />
      </main>

      {/* Footer */}
      <footer className="bg-gray-100 py-6">
        <div className="container mx-auto px-4 text-center text-sm text-gray-600">
          © {new Date().getFullYear()} Bookstore. All rights reserved.
        </div>
      </footer>
    </div>
  )
}
