// src/pages/ProfilePage.jsx
import React, { useEffect, useState } from 'react';
import api from '../api/axios';
import useAuth from '../hooks/useAuth';

export default function ProfilePage() {
  const { user } = useAuth();
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    if (!user) return;
    // เรียกข้อมูล profile จาก backend
    api
      .get('/users/profile', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      })
      .then(res => setProfile(res.data))
      .catch(console.error);
  }, [user]);

  if (!user) {
    return <p className="text-center mt-8">กรุณา <a href="/login" className="text-blue-600">เข้าสู่ระบบ</a> ก่อนดูข้อมูลส่วนตัว</p>;
  }

  if (!profile) {
    return <p className="text-center mt-8">กำลังโหลดข้อมูล...</p>;
  }

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-semibold mb-4">Your Profile</h2>
      <p><strong>Username:</strong> {profile.username}</p>
      <p><strong>Email:</strong> {profile.email}</p>
      <p><strong>Name:</strong> {profile.fname} {profile.lname}</p>
      <p><strong>Phone:</strong> {profile.phone}</p>
      {/* ถ้ามีที่อยู่ก็ render เพิ่มได้ */}
    </div>
  );
}
