// src/components/Layout.jsx
import React, { useState, useEffect } from 'react'
import { Link, Outlet, useNavigate } from 'react-router-dom'
import { Menu, Home, User, ShoppingCart, X, LogOut } from 'lucide-react'

export default function Layout() {
  const [drawerOpen, setDrawerOpen] = useState(false)
  const [categories, setCategories] = useState([])
  const navigate = useNavigate()
  const token = localStorage.getItem('token')
  let user = null
  if (token) {
    try {
      const payload = JSON.parse(atob(token.split('.')[1]))
      user = { username: payload.username }
    } catch { user = null }
  }

  useEffect(() => {
    fetch('/api/categories')
      .then(r => r.json())
      .then(setCategories)
      .catch(console.error)
  }, [])

  const handleLogout = () => {
    localStorage.removeItem('token')
    setDrawerOpen(false)
    navigate('/')
  }

  return (
    <div className="flex flex-col min-h-screen">
      {/* Top bar */}
      <header className="border-b">
        <div className="container mx-auto px-4 flex items-center justify-between h-16">
          <div className="flex items-center">
            <button onClick={() => setDrawerOpen(true)} className="md:hidden p-2">
              <Menu className="h-6 w-6 text-gray-600" />
            </button>
            <Link to="/" className="ml-2">
              <Home className="h-8 w-8 text-gray-600" />
            </Link>
          </div>
          <div className="hidden md:block flex-1 mx-8">
            <input
              type="search"
              placeholder="Search"
              className="w-full border border-gray-300 rounded px-4 py-2"
            />
          </div>
          <div className="flex items-center space-x-4">
            <button onClick={() => navigate(user ? '/profile' : '/login')} className="p-2">
              <User className="h-6 w-6 text-gray-600" />
            </button>
            <button onClick={() => navigate(user ? '/cart' : '/login')} className="p-2">
              <ShoppingCart className="h-6 w-6 text-gray-600" />
            </button>
          </div>
        </div>
      </header>

      {/* Sidebar */}
      {drawerOpen && (
        <div className="fixed inset-0 z-40 flex">
          <aside className="w-64 bg-white p-6 shadow-lg flex flex-col">
            <div className="flex items-center justify-between mb-6">
              {user ? (
                <div className="flex items-center space-x-2">
                  <User className="h-6 w-6 text-gray-600" />
                  <span className="font-semibold">Hello, {user.username}</span>
                </div>
              ) : (
                <span className="font-semibold">Hello, User</span>
              )}
              <button onClick={() => setDrawerOpen(false)}>
                <X className="h-6 w-6 text-gray-600" />
              </button>
            </div>

            <nav className="flex-1 overflow-auto space-y-1">
              {categories.map(cat => (
                <Link
                  key={cat.categoryID}
                  to={`/categories/${cat.categoryID}`}
                  className="block px-2 py-1 hover:bg-gray-100 rounded"
                  onClick={() => setDrawerOpen(false)}
                >
                  {cat.category_name}
                </Link>
              ))}
            </nav>

            <div className="mt-auto">
              {user ? (
                <button
                  onClick={handleLogout}
                  className="flex items-center space-x-1 text-red-600"
                >
                  <LogOut className="h-5 w-5" />
                  <span>Logout</span>
                </button>
              ) : (
                <Link
                  to="/login"
                  className="block px-2 py-1 hover:bg-gray-100 rounded"
                  onClick={() => setDrawerOpen(false)}
                >
                  Sign In
                </Link>
              )}
            </div>
          </aside>
          <div className="flex-1" onClick={() => setDrawerOpen(false)} />
        </div>
      )}

      {/* Nested pages render here */}
      <main className="flex-1 p-4">
        <Outlet />
      </main>
    </div>
  )
}
